import socket
import time

sck= socket.socket()
sck.connect(('192.168.1.10',102))

def sendPacket(samp):
        words=samp
        bytearr = bytes()
        try:
            if(len(words)%2==1):
                words=words+'0'
            bytearr = bytearr + bytes.fromhex(words)
        except Exception as e:
            print('MALFORMED INPUT',e)

        # print('bytes sent: ', bytearr)
        # print(len(bytearr))
        try:
            (sck).send(bytearr)
            time.sleep(10)
        except:
            print('SOCKET CLOSED')
        # self.messagesSent.append(bytearr)
        
        return 0

